programlistingco.programlisting:
@RooJavaBean
@RooToString
@RooJpaActiveRecord
public class Entity1 {

        @Size(max = 20)
        @NotNull
        private String name;

        @Size(max = 20)
        private String apellidos;
}

@RooJavaBean
@RooToString
@RooJpaActiveRecord
public class Pizza {

    @NotNull
    @Column(name = "pizza_nombre")
    @Size(min = 2)
    @Pattern(regexp = "[A-Za-z0-9].*")
    private String name;

    private Float price;

    @ManyToMany(cascade = CascadeType.ALL)
    private Set<Topping> toppings = 
      new HashSet<Topping>();

    @ManyToOne(targetEntity = Base.class)
    @JoinColumn
    private Base base;
}

